$("#contactForm").validator().on("submit", function (event) {
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        submitMSG(false, "Did you fill in the form properly?");
    } else {
        // everything looks good!
        event.preventDefault();
        $('#form-submit').attr("disabled", true);
        submitForm();
        $('#contactForm')[0].reset();
    }
});


async function submitForm() {
    let form = document.getElementById("contactForm");

    let data = {}
    for (let inp of form.querySelectorAll("input")) {
        data[inp.id] = inp.value;
    }
    data["message"] = form.querySelector("textarea").value;

    await fetch('https://api.useplunk.com/v1/send', {
        method: 'POST',
        body: JSON.stringify({
            to: "panchadip125@gmail.com",
            subject: `Contact Form Message: ${data.subj}`,
            body: `Name: ${data.inputName} | Email:  ${data.inputEmail} | Message: ${data.message}`
        }),
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer sk_b4eed4c94a1818a80f7f0ae63d0ad58fea9f6bccfe693b06',
        },
    });

    submitMSG(true, "Form Submitted");
}

function formSuccess() {
    $("#contactForm")[0].reset();
    submitMSG(true, "Form Submitted!")
}

function formError() {
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function () {
        $(this).removeClass();
    });
}

function submitMSG(valid, msg) {
    if (valid) {
        var msgClasses = "h3 text-left animated bounce text-dark";
    } else {
        var msgClasses = "h3 text-left text-dark";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}
